import job from "../../img/job.jpeg";
import { Jobstype } from "../../types";
import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Stack from "@mui/material/Stack";
import Grid from "@mui/material/Grid";
import LinearProgress, {
  linearProgressClasses,
} from "@mui/material/LinearProgress";
import { styled } from "@mui/material/styles";

const BorderLinearProgress = styled(LinearProgress)(({ theme }) => ({
  height: 10,
  borderRadius: 5,
  [`&.${linearProgressClasses.colorPrimary}`]: {
    backgroundColor:
      theme.palette.grey[theme.palette.mode === "light" ? 200 : 800],
  },
  [`& .${linearProgressClasses.bar}`]: {
    borderRadius: 5,
    backgroundColor: theme.palette.mode === "light" ? "#1a90ff" : "#308fe8",
  },
}));
type jobTypes = {
  jobs?: any;
  handleApply?: any;
};
const JobCard = ({ jobs, handleApply }: jobTypes) => {
  return (
    <>
      <Grid container spacing={2} sx={{ px: 2, pt: 2 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography
                gutterBottom
                variant="body1"
                sx={{ color: "#101928" }}
              >
                Posted on 29/03/2022
              </Typography>
              <Typography
                variant="h5"
                sx={{ color: "#101928", fontStyle: "bold" }}
              >
                Seinor Product Designer
              </Typography>
              <Typography variant="body1" sx={{ py: 2 }} color="primary">
                Kinshasa
              </Typography>
              <BorderLinearProgress variant="determinate" value={50} />
              <Stack direction="row" alignItems="center" spacing={1}>
                <Typography variant="body1" color="primary">
                  50 Applied of
                </Typography>
                <Typography variant="body1">200 Capacity</Typography>
              </Stack>
            </CardContent>
            <CardActions>
              <Stack direction="row">
                <Typography variant="body1" justifyContent="flex-end">
                  Expires on 30/04/2022
                </Typography>
                <Button color="primary">Apply</Button>
              </Stack>
            </CardActions>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                Lizard
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Lizards are a widespread group of squamate reptiles, with over
                6,000 species, ranging across all continents except Antarctica
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Share</Button>
              <Button size="small">Learn More</Button>
            </CardActions>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                Lizard
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Lizards are a widespread group of squamate reptiles, with over
                6,000 species, ranging across all continents except Antarctica
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Share</Button>
              <Button size="small">Learn More</Button>
            </CardActions>
          </Card>
        </Grid>
      </Grid>
    </>
  );
};
export default JobCard;
